<script setup lang="ts">
import type { ThreeEvent } from '@tresjs/core'
import { OrbitControls } from '@tresjs/cientos'
import { TresCanvas } from '@tresjs/core'
import { ref } from 'vue'

function onClick(ev: ThreeEvent<MouseEvent>) {
  ev.eventObject.material.color.set('#008080')
}

function onDoubleClick(ev: ThreeEvent<MouseEvent>) {
  ev.eventObject.material.color.set('#FFD700')
}

function onPointerEnter(ev: ThreeEvent<MouseEvent>) {
  ev.eventObject.material.color.set('#CCFF03')
}

function onPointerLeave(ev: ThreeEvent<MouseEvent>) {
  ev.eventObject.material.color.set('#efefef')
}

function onPointerMove(ev: ThreeEvent<MouseEvent>) {
  ev.eventObject.material.color.set('#00F')
}

function onContextMenu(ev: ThreeEvent<MouseEvent>) {
  ev.eventObject.material.color.set('#FF4500')
}

const visible = ref(false)
setInterval(() => {
  visible.value = !visible.value
}, 1000)
</script>

<template>
  <OverlayInfo>
    <h1><code>eventManager.remove</code></h1>
    <p>When an object is removed from the scene, it should trigger <code>@pointer{out,leave}</code> events, if necessary.</p>
    <h2>Setup</h2>
    <p>A few boxes are added/removed from the scene. Their events "bubble up" to the larger parent box.</p>
    <p>Moving the pointer over the small boxes should trigger <code>@pointerenter</code> on the large parent box. If the pointer is still over a small box when it is removed, <code>@pointerleave</code> should be called on the small box and "bubbled up" to the larger parent box.</p>
    <img src="data:image/png;base64," />
    <img src="data:image/png;base64," />
  </OverlayInfo>
  <TresCanvas
    window-size
  >
    <TresPerspectiveCamera
      :position="[11, 11, 11]"
      :look-at="[0, 0, 0]"
    />
    <OrbitControls />
    <TresMesh
      @click="onClick"
      @doubleclick="onDoubleClick"
      @pointerenter="onPointerEnter"
      @pointerleave="onPointerLeave"
      @pointermove="onPointerMove"
      @contextmenu="onContextMenu"
    >
      <TresMesh
        v-if="visible"
        :position-x="2"
        :scale="0.5"
        @click="onClick"
        @doubleclick="onDoubleClick"
        @pointerenter="onPointerEnter"
        @pointerleave="onPointerLeave"
        @pointermove="onPointerMove"
        @contextmenu="onContextMenu"
      >
        <TresBoxGeometry :args="[1, 1, 1]" />
        <TresMeshToonMaterial color="#efefef" />
        <TresMesh
          :position-x="2"
          :scale="0.5"
          @click="onClick"
          @doubleclick="onDoubleClick"
          @pointerenter="onPointerEnter"
          @pointerleave="onPointerLeave"
          @pointermove="onPointerMove"
          @contextmenu="onContextMenu"
        >
          <TresBoxGeometry :args="[1, 1, 1]" />
          <TresMeshToonMaterial color="#efefef" />
        </TresMesh>
      </TresMesh>
      <TresBoxGeometry :args="[1, 1, 1]" />
      <TresMeshToonMaterial color="#efefef" />
    </TresMesh>
    <TresDirectionalLight :intensity="1" />
    <TresAmbientLight :intensity="1" />
  </TresCanvas>
</template>

<style scoped>
img {
  border: 2px solid #ccc;
}
</style>
